import React, { useState } from "react";

function AdminDashboard() {
    const [campaignName, setCampaignName] = useState("");
    const [emailTemplate, setEmailTemplate] = useState("Password Expiry Notification");
    const [targetGroup, setTargetGroup] = useState("All Employees");
    const [numberOfEmails, setNumberOfEmails] = useState("");
    const [successLink, setSuccessLink] = useState("");
    const [showSuccess, setShowSuccess] = useState(false);
    const [reports, setReports] = useState([]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const campaignData = {
            campaign_name: campaignName,
            email_template: emailTemplate,
            target_group: targetGroup,
            numberof_emails: parseInt(numberOfEmails)
        };

        try {
            const response = await fetch('http://localhost:5000/api/save_campaign', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(campaignData)
            });

            const result = await response.json();

            if (response.ok) {
                const link = window.location.origin + "/FRONTEND/feedBack.html";
                setSuccessLink(link);
                setShowSuccess(true);

                // Add new report to the table
                setReports(prev => [
                    ...prev,
                    {
                        campaign_name: campaignData.campaign_name,
                        sent_date: new Date().toLocaleDateString(),
                        emails_sent: campaignData.numberof_emails,
                        clicked_link: "--",
                        vulnerability_rate: "--"
                    }
                ]);

                // Optionally reset form
                setCampaignName("");
                setEmailTemplate("Password Expiry Notification");
                setTargetGroup("All Employees");
                setNumberOfEmails("");
            } else {
                alert('Error: ' + result.message);
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Failed to connect to the server.');
        }
    };

    return (
        <>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <div className="container-fluid">
                    <a className="navbar-brand" href="#">Phishing Simulator Admin</a>
                    <div className="d-flex">
                        <a href="login.html" className="btn btn-outline-light btn-sm">Logout</a>
                    </div>
                </div>
            </nav>

            <div className="container mt-4">
                <ul className="nav nav-tabs" id="adminTabs" role="tablist">
                    <li className="nav-item" role="presentation">
                        <button className="nav-link active" id="create-tab" data-bs-toggle="tab" data-bs-target="#create"
                            type="button" role="tab">Create Campaign</button>
                    </li>
                    <li className="nav-item" role="presentation">
                        <button className="nav-link" id="reports-tab" data-bs-toggle="tab" data-bs-target="#reports" type="button"
                            role="tab">Awareness Reports</button>
                    </li>
                </ul>
                <div className="tab-content p-3 border border-top-0 rounded-bottom" id="myTabContent">
                    <div className="tab-pane fade show active" id="create" role="tabpanel">
                        <h4>Create New Phishing Simulation</h4>
                        <form className="mt-3" onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label className="form-label">Campaign Name</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="e.g., 'Urgent Payroll Update'"
                                    required
                                    value={campaignName}
                                    onChange={e => setCampaignName(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <label className="form-label">Email Template</label>
                                <select
                                    className="form-select"
                                    value={emailTemplate}
                                    onChange={e => setEmailTemplate(e.target.value)}
                                >
                                    <option>Password Expiry Notification</option>
                                    <option>Urgent Document Shared</option>
                                    <option>Unusual Sign-in Activity</option>
                                </select>
                            </div>
                            <div className="mb-3">
                                <label className="form-label">Target Group</label>
                                <select
                                    className="form-select"
                                    value={targetGroup}
                                    onChange={e => setTargetGroup(e.target.value)}
                                >
                                    <option>All Employees</option>
                                    <option>Finance Dept</option>
                                    <option>IT Dept</option>
                                </select>
                            </div>
                            <div className="mb-3">
                                <label className="form-label">Number of Email</label>
                                <input
                                    type="number"
                                    name="emailNumber"
                                    className="form-control"
                                    placeholder="Enter number of emails"
                                    required
                                    value={numberOfEmails}
                                    onChange={e => setNumberOfEmails(e.target.value)}
                                />
                            </div>
                            <button type="submit" className="btn btn-success">Launch Campaign</button>
                        </form>
                        {showSuccess && (
                            <div className="alert alert-success mt-3">
                                Campaign launched! Simulation link generated: <strong>{successLink}</strong>
                            </div>
                        )}
                    </div>

                    <div className="tab-pane fade" id="reports" role="tabpanel">
                        <h4>Campaign Performance & Awareness Reports</h4>
                        <table className="table table-striped mt-3">
                            <thead>
                                <tr>
                                    <th>Campaign Name</th>
                                    <th>Sent Date</th>
                                    <th>Emails Sent</th>
                                    <th>Clicked Link</th>
                                    <th>Vulnerability Rate</th>
                                </tr>
                            </thead>
                            <tbody>
                                {reports.map((report, idx) => (
                                    <tr key={idx}>
                                        <td>{report.campaign_name}</td>
                                        <td>{report.sent_date}</td>
                                        <td>{report.emails_sent}</td>
                                        <td>{report.clicked_link}</td>
                                        <td>{report.vulnerability_rate}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    );
}

export default AdminDashboard;
