function loginPage() {
    document.getElementById('loginForm').addEventListener('submit', function (e) {
        e.preventDefault();
        const username = document.getElementById('username').value.toLowerCase();
        // Simple frontend routing for demo purposes
        if (username === 'admin') window.location.href = 'admin_dashboard.html';
        else if (username === 'hr') window.location.href = 'admin_dashboard.html'; // HR shares similar view for reports
        else if (username === 'employee') window.location.href = 'employee_dashboard.html';
        else alert('Invalid user for demo. Try admin, hr, or employee.');
    });

    function showSignUp() {
        const cardBody = document.querySelector('.card-body');
        const cardBody2 = document.querySelector('.card-body2');
        const cardBody3 = document.querySelector('.card-body3');
        let forgetPassLinks = document.querySelectorAll('.forgetPass');
        if (cardBody.style.display !== 'none') {
            cardBody.style.display = 'none';
            cardBody3.style.display = 'none';
            cardBody2.style.display = 'block';
        } else {
            cardBody.style.display = 'block';
            cardBody2.style.display = 'none';
            cardBody3.style.display = 'none';
        }

        forgetPassLinks.forEach(link => {
            link.addEventListener('click', function (e) {
                e.preventDefault();
                console.log("forget passwor");
                cardBody.style.display = 'none';
                cardBody2.style.display = 'none';
                cardBody3.style.display = 'block';
            });
        });
    }
    return (
        <>
            <nav class="navbar navbar-expand-lg navbar-dark bg-dark sticky-top">
                <div class="container">
                    <a class="navbar-brand d-flex align-items-center" href="#">
                        <i class="bi bi-shield-lock-fill me-2"></i>
                        PhishAware
                    </a>
                    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
                        <span class="navbar-toggler-icon"></span>
                    </button>
                    <div class="collapse navbar-collapse justify-content-end" id="navbarNav">
                        <ul class="navbar-nav">
                            <li class="nav-item"><a class="nav-link active" href="./index.html">Home</a></li>
                            <li class="nav-item"><a class="nav-link" href="./index.html#features">Features</a></li>
                            <li class="nav-item"><a class="nav-link" href="./index.html#about">About</a></li>
                            <li class="nav-item ms-lg-3">
                                <a class="btn btn-primary" href="login.html">Login to Portal</a>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
            <main class="mainArea">
                <div class="container">
                    <div class="card login-card">

                        <div class="card-body">
                            <h3 class="text-center mb-4">Awareness Portal Login</h3>
                            {/* {   <!-- login form -->} */}
                            <form id="loginForm">
                                <div class="mb-3">
                                    <label for="username" class="form-label">Username</label>
                                    <input type="text" class="form-control" id="username" required />
                                </div>
                                <div class="mb-3">
                                    <label for="password" class="form-label">Password</label>
                                    <input type="password" class="form-control" id="password" required />
                                </div>
                                <button type="submit" class="btn btn-primary w-100">Login</button>
                            </form>
                            <div class="mt-3 text-center d-flex justify-content-between">
                                <a href="#" class="forgetPass">Forgot Password?</a>
                                <a href="#" onclick="showSignUp()" class="ms-3">Sign Up</a>
                            </div>
                        </div>

                        <div class="card-body2" style="display:none;">
                            <h3 class="text-center mb-4">Awareness Portal Login</h3>
                            {/* <!-- Sign up form --> */}
                            <form id="loginForm2">
                                <div class="mb-3">
                                    <label for="username" class="form-label">Username</label>
                                    <input type="text" class="form-control" id="SignUsername" required />
                                </div>
                                <div class="mb-3">
                                    <label for="email" class="form-label">Email</label>
                                    <input type="email" class="form-control" id="email" required />
                                </div>
                                <div class="mb-3">
                                    <label for="password" class="form-label">Password</label>
                                    <input type="password" class="form-control" id="SignPassword" required />
                                </div>
                                <div class="mb-3">
                                    <label for="confirmPassword" class="form-label">Confirm Password</label>
                                    <input type="password" class="form-control" id="confirmPassword" required />
                                </div>
                                <button type="submit" class="btn btn-primary w-100">Sign UP</button>
                            </form>
                            <div class="mt-3 text-center d-flex justify-content-between">
                                <a href="#" class="forgetPass">Forgot Password?</a>
                                <a href="#signup.html" onclick="showSignUp()" class="ms-3">Login</a>
                            </div>
                        </div>

                        <div class="card-body3" style="display:none;">
                            <h3 class="text-center mb-4">Awareness Portal Login</h3>
                            { /* Forget password form */}
                            <form id="loginForm3">
                                <div class="mb-3">
                                    <label for="email" class="form-label">Email</label>
                                    <input type="email" class="form-control" id="forgetEmail" required />
                                </div>
                                <button type="submit" class="btn btn-primary w-100">Submit</button>
                            </form>
                            <div class="mt-3 text-center d-flex justify-content-between">
                                <a href="#" onclick="showSignUp()" class="ms-3">Login</a>
                            </div>
                        </div>

                    </div>
                </div>
            </main>
        </>
    )
}
