import React, { useState } from "react";
import '../../Styling/Frontend/'

function LoginPage() {
    const [view, setView] = useState("login");
    const [loginUsername, setLoginUsername] = useState("");
    const [loginPassword, setLoginPassword] = useState("");
    const [signUsername, setSignUsername] = useState("");
    const [signEmail, setSignEmail] = useState("");
    const [signPassword, setSignPassword] = useState("");
    const [signConfirmPassword, setSignConfirmPassword] = useState("");
    const [forgetEmail, setForgetEmail] = useState("");

    // Handle login form submit
    const handleLogin = (e) => {
        e.preventDefault();
        const username = loginUsername.toLowerCase();
        if (username === "admin" || username === "hr") {
            window.location.href = "/admin_dashboard.html";
        } else if (username === "employee") {
            window.location.href = "/employee_dashboard.html";
        } else {
            alert("Invalid user for demo. Try admin, hr, or employee.");
        }
    };

    // Handle sign up form submit
    const handleSignUp = (e) => {
        e.preventDefault();
        if (signPassword !== signConfirmPassword) {
            alert("Passwords do not match!");
            return;
        }
        // Implement sign up logic here
        alert("Sign up successful (demo only)");
        setView("login");
    };

    // Handle forgot password form submit
    const handleForget = (e) => {
        e.preventDefault();
        // Implement forgot password logic here
        alert("Password reset link sent (demo only)");
        setView("login");
    };

    return (
        <>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark sticky-top">
                <div className="container">
                    <a className="navbar-brand d-flex align-items-center" href="#">
                        <i className="bi bi-shield-lock-fill me-2"></i>
                        PhishAware
                    </a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
                        <ul className="navbar-nav">
                            <li className="nav-item"><a className="nav-link active" href="./index.html">Home</a></li>
                            <li className="nav-item"><a className="nav-link" href="./index.html#features">Features</a></li>
                            <li className="nav-item"><a className="nav-link" href="./index.html#about">About</a></li>
                            <li className="nav-item ms-lg-3">
                                <a className="btn btn-primary" href="login.html">Login to Portal</a>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
            <main className="mainArea">
                <div className="container">
                    <div className="card login-card">
                        {view === "login" && (
                            <div className="card-body">
                                <h3 className="text-center mb-4">Awareness Portal Login</h3>
                                <form onSubmit={handleLogin}>
                                    <div className="mb-3">
                                        <label htmlFor="username" className="form-label">Username</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="username"
                                            value={loginUsername}
                                            onChange={e => setLoginUsername(e.target.value)}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label htmlFor="password" className="form-label">Password</label>
                                        <input
                                            type="password"
                                            className="form-control"
                                            id="password"
                                            value={loginPassword}
                                            onChange={e => setLoginPassword(e.target.value)}
                                            required
                                        />
                                    </div>
                                    <button type="submit" className="btn btn-primary w-100">Login</button>
                                </form>
                                <div className="mt-3 text-center d-flex justify-content-between">
                                    <a href="#" className="forgetPass" onClick={e => { e.preventDefault(); setView("forget"); }}>Forgot Password?</a>
                                    <a href="#" className="ms-3" onClick={e => { e.preventDefault(); setView("signup"); }}>Sign Up</a>
                                </div>
                            </div>
                        )}

                        {view === "signup" && (
                            <div className="card-body2">
                                <h3 className="text-center mb-4">Sign Up</h3>
                                <form onSubmit={handleSignUp}>
                                    <div className="mb-3">
                                        <label htmlFor="SignUsername" className="form-label">Username</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="SignUsername"
                                            value={signUsername}
                                            onChange={e => setSignUsername(e.target.value)}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label htmlFor="email" className="form-label">Email</label>
                                        <input
                                            type="email"
                                            className="form-control"
                                            id="email"
                                            value={signEmail}
                                            onChange={e => setSignEmail(e.target.value)}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label htmlFor="SignPassword" className="form-label">Password</label>
                                        <input
                                            type="password"
                                            className="form-control"
                                            id="SignPassword"
                                            value={signPassword}
                                            onChange={e => setSignPassword(e.target.value)}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label htmlFor="confirmPassword" className="form-label">Confirm Password</label>
                                        <input
                                            type="password"
                                            className="form-control"
                                            id="confirmPassword"
                                            value={signConfirmPassword}
                                            onChange={e => setSignConfirmPassword(e.target.value)}
                                            required
                                        />
                                    </div>
                                    <button type="submit" className="btn btn-primary w-100">Sign Up</button>
                                </form>
                                <div className="mt-3 text-center d-flex justify-content-between">
                                    <a href="#" className="forgetPass" onClick={e => { e.preventDefault(); setView("forget"); }}>Forgot Password?</a>
                                    <a href="#" className="ms-3" onClick={e => { e.preventDefault(); setView("login"); }}>Login</a>
                                </div>
                            </div>
                        )}

                        {view === "forget" && (
                            <div className="card-body3">
                                <h3 className="text-center mb-4">Forgot Password</h3>
                                <form onSubmit={handleForget}>
                                    <div className="mb-3">
                                        <label htmlFor="forgetEmail" className="form-label">Email</label>
                                        <input
                                            type="email"
                                            className="form-control"
                                            id="forgetEmail"
                                            value={forgetEmail}
                                            onChange={e => setForgetEmail(e.target.value)}
                                            required
                                        />
                                    </div>
                                    <button type="submit" className="btn btn-primary w-100">Submit</button>
                                </form>
                                <div className="mt-3 text-center d-flex justify-content-between">
                                    <a href="#" className="ms-3" onClick={e => { e.preventDefault(); setView("login"); }}>Login</a>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </main>
        </>
    );
}

export default LoginPage;
